<template>
	<view :class="['digital-keyboard',{'keyboard-show':!keyBoardSwitch}]" @tap.stop="">
		<view class="list" :hover-start-time='0' @tap.stop="setKey(key)" hover-stay-time="200" :style="{background:key=='.'||key==='' ?'rgba(216, 216, 216, 0)':'#fff'}"
		 v-for="(key,index) in data" :key='index'>{{key=='.'&&!decimalPoint?'':key}}</view>
	</view>
</template>

<script>
	export default {
		props: ['keyBoardSwitch', 'decimalPoint'],
		data() {
			return {
				data: [1, 2, 3, 4, 5, 6, 7, 8, 9, '.', 0, ''],
				value: '',
			}
		},
		methods: {
			setKey(k) {
				if (k === '.' && !this.decimalPoint) {
					return;
				}
				k === '' ? this.value = this.value.substring(0, this.value.length - 1) : this.value += k.toString();
				this.$emit('change', k)
			},
		},
		watch: {
			keyBoardSwitch: function() {
				if (!this.keyBoardSwitch) this.$emit('closeKeyboard', this.value);
			}
		}
	}
</script>

<style lang="scss">
	.digital-keyboard {
		width: 100vw;
		position: fixed;
		bottom: 0;
		left: 0;
		overflow: hidden;
		display: flex;
		justify-content: space-around;
		flex-wrap: wrap;
		padding-bottom: 10upx;
		background: #D8D8D8;
		height: 450upx !important;

		.list {
			width: 32%;
			margin-top: 10upx;
			height: 100upx;
			line-height: 100upx;
			// box-sizing: border-box;
			border-radius: 8upx;
			float: left;
			text-align: center;
			font-size: 24px;
			// border-left:1px solid #c8c8c8;
			// border-bottom: 1px solid #c8c8c8;
			background: #fff;

			&:nth-child(3n) {
				border-right: 1px solid #c8c8c8;
			}

			&:last-child {
				position: relative;

				&:before {
					content: '';
					position: absolute;
					left: 50%;
					top: 50%;
					transform: translate(-50%, -50%);
					width: 50upx;
					height: 50upx;
					background: url('data:image/png;base64,')no-repeat;
					background-size: 100% 100%;
				}
			}
		}

		
	}

	.keyboard-show {
		height: 0;
	}
</style>
